document.querySelector('#Midgard').addEventListener('click', (e) => chooseStory(story = 'Midgard'));
document.querySelector('#Alfheim').addEventListener('click', (e) => chooseStory(story = 'Alfheim'));
document.querySelector('#Muspellheim').addEventListener('click', (e) => chooseStory(story = 'Muspellheim'));
document.querySelector('#Vanaheim').addEventListener('click', (e) => chooseStory(story = 'Vanaheim'));
document.querySelector('#Svartalfheim').addEventListener('click', (e) => chooseStory(story = 'Svartalfheim'));
document.querySelector('#Jotunheim').addEventListener('click', (e) => chooseStory(story = 'Jotunheim'));
document.querySelector('#Helheim').addEventListener('click', (e) => chooseStory(story = 'Helheim'));
document.querySelector('#Niflheim').addEventListener('click', (e) => chooseStory(story = 'Niflheim'));
let div = document.getElementById('markerForText');

const massiveStory = [];

localStorage.setItem("img", 0); // локальное хранилище
let imgF = localStorage.getItem("img"); // retrieve variable
document.querySelector('#testImg').addEventListener('click', testImgF);
function testImgF() {
    let tree = document.querySelector('.Tree-Ygg');
    if (imgF == 0) {
        tree.style.display = 'table';
        imgF++;
    } else {
        tree.style.display = 'none';
        imgF--;
    }
}

function chooseStory(story) {
    switch (story) {
        case ('Asgard'):
            massiveStory[0] = '<p>А́сгард (уст. Азгард; др.-сканд. Ásgarðr — «ограждённый участок асов») — в скандинавской мифологии небесный город, обитель богов-асов.</p>';
            massiveStory[1] = '<p>Асы — существа порядка, ведя войну с ванами — существами природы, построили укреплённый Асгард. Позже асы сдружились с ванами, обменялись представителями (а на самом деле — заложниками, так в мир асов пришёл ван Ньёрд, иначе — Ниодр, Ниорд и т. д., и поселился с ними в Асгарде) и с тех пор живут в мире друг с другом. Кроме богов и богинь, в Асгарде (в чертоге Вальгаллы) живут девы-воительницы — валькирии. Другая группа богов, ваны, живут в Ванахейме. Один из трёх корней Древа Мира — Иггдрасиля — тянется в сторону Асгарда.</p>';
            massiveStory[2] = '<p>Асгард, Мидгард и Хельхейм — космогоническая «вертикаль» мифологического хронотопа, уходящая в глубокую архаику древнего индоевропейского религиозного единства, объединённая творческой волей богов-созидателей, образом и символикой Иггдрасиля и судьбой людей. Кроме того, особое благоволение асов людям выразилось и в том, что Асгард и Мидгард связаны природным явлением — мостом радуги (Биврёст — др.-исл. «радуга»), стражем которого поставлен бог Хеймдалль. Судьба Мидгарда и Асгарда едина: им отведён один век, и когда в конце времён в ходе великой битвы богов и гримтурсенов-ётунов погибнет Мидгард, рухнет источенный клыками дракона Нидхёгга великий ясень Иггдрасиль и вместе с ним обрушится построенный вокруг его кроны Асгард, — так рассказывает поэма «Пророчество вёльвы», пересказанная Снорри Стурлусоном в XIII веке.</p>';
            massiveStory[3] = '<p>Вальха́лла, также Валга́лла, Вальга́лла (др.-сканд. Valhǫll — «чертог мёртвых», «чертог убитых», «дворец убитых») — в германо-скандинавской мифологии небесный чертог в Асгарде, куда попадают после смерти павшие в битве воины, и где они продолжают прежнюю героическую жизнь.</p>';
            massiveStory[4] = '<p>О́дин правит Вальхаллой, восседая на Хлидскьялве. Он отбирает половину воинов, павших в бою, а валькирии доставляют их в чертог. Другая половина павших отправляется в Фолькванг (Fólkvangr — «Людское поле») к богине Фрейе. <br> Легенда гласит, что Вальхалла представляет собой гигантский зал, увешанный щитами на остриях мечей. У этого зала 540 дверей, и через каждую выйдут 800 воинов по зову бога Хеймдалля во время последней битвы — Рагнарёка. <br> оины, обитающие в Вальхалле, зовутся эйнхерии. Каждый день с утра они облачаются в доспехи и сражаются насмерть. Другие источники говорят, что они переродились. Едят они мясо вепря Сехримнира, которого забивают каждый день и который каждый день воскресает. Пьют же эйнхерии мёд, которым доится коза Хейдрун, стоящая в Вальхалле и жующая листья Мирового Древа Иггдрасиль. Вальхаллу вместо огня освещает блеск мечей.</p>'
            massiveStory[5] = '<img src="Picture/Asgard.jpg">'
            div.innerHTML = massiveStory.join('');
            break;
        case ('Midgard'):
            massiveStory[0] = '<p>Ми́дгард (др.-сканд. Miðgarðr — букв. «среднее огороженное пространство» — «срединная земля») в германо-скандинавской мифологии — мир, населённый людьми.</p>';
            massiveStory[1] = '<p>Германо-скандинавская космогония рисует циклопический процесс создания космоса из мировой бездны Гинунгагап (др.-исл. «зияющая бездна»), когда из смешения искр огня из царства Муспельхейм и льда из царства Нифльхейм возникло первое в мире живое существо — великан Имир. Именно из его тела возникшие много позже братья-боги — Один, Вили и Ве — сотворили «свой» мир: плоскую землю, небо и Ётунхейм, заморское королевство великанов — ётунов. Создав людей, боги поселили их на земле, в срединном мире, который огородили от йотунов стеной, изготовленной из век убитого ими Имира. Так земля и получила имя Мидгард. Мидгарду боги благоволили и соединили его с небом радужным мостом, которому дали имя Биврёст (др.-исл. «радуга»).</p>';
            massiveStory[2] = '<p>Мидгард отгорожен от других миров не только стеной из ресниц Имира. Расположенный на плоском круге, он окружен водами мирового океана, на дне которого лежит Ёрмунганд, ещё один отпрыск Локи, — Мировой змей, окольцевавший землю и держащий в зубах собственный хвост (одна из вариаций образа Уробороса, одного из самых древних и сложных эзотерических символов евразийской культуры).</p>';
            massiveStory[3] = '<p>Такое соседство — не столько защита для Мидгарда, сколько страшная опасность, от которой мир людей старается защищать грозный бог Тор (др-исл. «гром»). Всплыв в конце времён из Мирового океана, Ёрмунганд вызовет гибельные наводнения, наступит трёхлетняя зима (фимбульвинтер), Солнце погаснет, звёзды попадают с неба; между богами и великанами произойдёт последняя битва — Рагнарок (др.-исл. Ragnarök); боги победят чудовищ и йотунов, но и сами погибнут. Рухнет Иггдрасиль (священный ясень, Мировое Древо) и обрушит небо; огненный меч великана Сурта сожжёт мир, и Мидгард погрузится в волны мирового океана. Так рассказывает поэма «Прорицание вёльвы» (иначе — «Пророчество вёльвы», Völuspá), пересказанная Снорри Стурлусоном в его «Круге земном» (XIII век).</p>';
            massiveStory[4] = '<p>Но это не конец Мидгарда. Новая земля возродится из волн мирового океана, и ею будут править боги, не совершавшие клятвопреступлений и ошибок, на ней будут жить новые счастливые люди в любви, мире и достатке.</p>';
            massiveStory[5] = '<img src="Picture/midgard.jpg">'
            div.innerHTML = massiveStory.join('');
            break;
        case ('Alfheim'):
            massiveStory[0] = '<p>Альвхе́йм (др.-сканд. álfheimr) — в скандинавской мифологии мир альвов, один из девяти миров древнескандинавской космологии (или одно из 14-ти жилищ богов-асов). Иногда, чтобы подчеркнуть, что там живут светлые альвы, употребляется название Льёсальвхе́йм (от др.-сканд. ljósálfar, светлые альвы).</p>';
            massiveStory[1] = '<p>В текстах едды часто упоминаются темные альвы (гномы), тогда как об Альвах из Альвгейма сказано очень мало. В «Песне о Велунде» упоминается собственно Велунд, как великий кузнец и король альвов.  Воздушный, легкий и светлый Альвгейм имеет свой природный антипод — Свартальвгейм, мир темных альвов, находящийся под землей. Альвы Свартальвгейма так и называются – темные альвы, гномы или твёрги. При этом альвов из Альвгейма в текстах едды называют просто «альвы», без определения «светлы». Это особенно интересно, потому что по исландским поверьям, существовавшим вплоть до конца XX века, альвы (или эльфы) живут не в небесах, а в холмах.</p>';
            massiveStory[2] = '<img style="width: auto; height: 85%;" src="Picture/Alfheim.jpg">'
            delete massiveStory[3];
            delete massiveStory[4];
            delete massiveStory[5];
            div.innerHTML = massiveStory.join('');
            break;
        case ('Muspellheim'):
            massiveStory[0] = '<p>Муспельхейм (Muspelheim, огненная земля) — в германо-скандинавской мифологии: один из девяти миров, страна огненных великанов, огненное царство, которым правит огненный великан Сурт («Чёрный»). В конце времён сыновья Муспелля проскачут через Мюрквид (мифический «Тёмный Лес») к Биврёсту, и от их скачков разрушится радужный мост асов. В условно-пространственной структуре миров Муспельхейм соответствует южной стороне от Мидгарда.</p>';
            massiveStory[1] = '<p>В Муспельхейме живут огненные великаны вместе со своим повелителем Суртом. Муспельхейм существовал вместе с Нифльхеймом ещё до появления всего живого.</p>';
            massiveStory[2] = '<p>По легендам, искры из Муспельхейма породили жизнь в талой воде. Также из искр асы создали звезды. Часть звезд они укрепили неподвижно, другие же, для того чтобы узнавать время, разместили так, чтобы они двигались по кругу, обходя его за один год.</p>';
            massiveStory[3] = '<img style="width: 90%; height: auto;" src="Picture/Myspel.jpg">'
            delete massiveStory[4];
            delete massiveStory[5];
            div.innerHTML = massiveStory.join('');
            break;
        case ('Vanaheim'):
            massiveStory[0] = '<p>Ванахейм (исл. Vanaheimr: страна ванов) — в германо-скандинавской мифологии родина Ванов.</p>';
            massiveStory[1] = '<p>Дело в том, что даже о создании Асгарда известно не так много, как хотелось бы, а времена его создания относят к чуть ли не доисторическим временам, когда еще даже не существовало письменности. Более того, когда Один еще даже не придумал использовать руны. Что уж говорить о Ванахейме, который считался еще более древним, чем Асгард.</p>';
            massiveStory[2] = '<p>Примечательно, что и асы и ваны были представителями богов. Но если асы были богами войны и всем, что с ней связано, то ваны были богами природных сил. Само собой, рано или поздно они должны были столкнуться друг с другом. Так оно и случилось. А началась заварушка с того, что ваны создали волшебницу Гулльвейгом, после чего послали ее в Мидгард, дабы испортить нравы людей, чтобы они чтили не только асов, но и ванов.</p>';
            massiveStory[3] = '<p>Само собой, асам это не понравилось и Один объявил войну ванам, сразив одного из них своим копьем. Примечательно, что к тому моменту Асгард еще не обладал своими знаменитыми стенами, поэтому ванам удалось осадить Асгард и даже проникнуть внутрь города. Однако внутри Асгарда Асы были сильнее. По сути, долгая и кровопролитная война свелась к тому, что мудрый Один предложил ванам заключить мир.</p>';
            massiveStory[4] = '<img style="width: 90%; height: auto;" src="Picture/vanaheim.jpg">'
            delete massiveStory[5];
            div.innerHTML = massiveStory.join('');
            break;
        case ('Svartalfheim'):
            massiveStory[0] = '<p>В скандинавской мифологии Нидавеллир – мир гномов (они же карлы или цверги). Нидавеллир упоминается в «Прорицании вёльвы» из «Старшей Эдды» в связи с родом Синдри (или Эйтри), того самого гнома, которого Локи поссорил с сыновьями Ивальди, в результате чего два древних племени устроили соревнование в кузнечном мастерстве.</p>';
            massiveStory[1] = '<p>В оригинальном эддическом отрывке сказано следующее: «Пред севером крайним, в Нидавеллире, стоит дом золотой, в котором род Синдри живет». На крайнем севере в соответствии с древнескандинавской космологической системой находился Нифльхейм – мир льда и мрака. То есть Нидавеллир должен располагаться в непосредственной близости от Нифльхейма.</p>';
            massiveStory[2] = '<img style="width: 90%; height: auto;" src="Picture/svart.jpg">'
            delete massiveStory[3];
            delete massiveStory[4];
            delete massiveStory[5];
            div.innerHTML = massiveStory.join('');
            break;
        case ('Jotunheim'):
            massiveStory[0] = '<p>Ётунхейм, Йотунхейм (то есть земля Ётунов; др.-сканд. Jötunheimr) в германо-скандинавской мифологии — один из девяти миров, земля, населённая великанами-ётунами.</p>';
            massiveStory[1] = '<p>По легендам был сотворён асами к востоку от Мидгарда для Бергельмира и его потомков. От Асгарда Ётунхейм отделяет река Ивинг. Ётунхеймом правит король Трим. Основное поселение Ётунхейма — Утгард. Также упоминаются расположенные там Железный лес и Каменные горы. В Ётунхейме расположены Гастропнир — дом Менглад и Тримхейм — дом Тьяцци.</p>';
            massiveStory[2] = '<p>Часто посещался асами. В сагах упоминается неоднократные путешествия Тора и Локи по Ётунхейму, в том числе и в Утгард. Стихия этого мира — Земля. Часть Ётунов жила в Каменных горах; считалось, что голова и сердце их были каменными. В Ётунхейме есть место — Железный лес, место обитания ведьм (ночных наездниц), женщин-троллей. Из Ётунхейма пришли норны. С их приходом закончился на земле золотой век и поделилось время на прошлое, настоящее и будущее. Появились рождение и смерть.</p>';
            massiveStory[3] = '<img style="width: 90%; height: auto;" src="Picture/jotunheim.jpg">'
            delete massiveStory[4];
            delete massiveStory[5];
            div.innerHTML = massiveStory.join('');
            break;
        case ('Helheim'):
            massiveStory[0] = '<p>Хельхейм (прагерм. Helheim, буквально Владения Хель) — в германо-скандинавской мифологии один из девяти миров, мир мёртвых, в Хельхейм бог Один низверг великаншу Хель, поэтому она там властвует.</p>';
            massiveStory[1] = '<p>Это холодное, тёмное и туманное место, куда попадают все умершие, кроме героев, принятых в эйнхерии. Он окружён непроходимой рекой Гьёлль. Ни одно существо, даже боги, не может вернуться из Хельхейма. Вход в Хельхейм охраняется Гармом, чудовищной собакой, и великаншей Модгуд.</p>';
            massiveStory[2] = '<p>В день Рагнарёк из Хельхейма на корабле Нагльфар воинство Хель выплывет для сражения с асами.</p>';
            massiveStory[3] = '<img style="width: 90%; height: auto;" src="Picture/helheim.jpg">'
            delete massiveStory[4];
            delete massiveStory[5];
            div.innerHTML = massiveStory.join('');
            break;
        case ('Niflheim'):
            massiveStory[0] = '<p>Ни́фльхе́йм (др.-сканд. Niflheimr — обитель туманов) — в германо-скандинавской мифологии один из девяти миров вселенной, земля льдов и туманов, место обитания помещённых туда асами великанов-ётунов и богини Хель.</p>';
            massiveStory[1] = '<p>В сказаниях говорится, что Нифльхейм находился к северу от бездны Гиннунгагап. А к югу от бездны располагался Муспельхейм.</p>';
            massiveStory[2] = '<p>По преданиям, в начале в Нифльхейме забил источник Хвергельмир («кипящий котёл»). Мороз превращал воду в лёд, но источник бил не переставая и ледяные глыбы подвигались к Муспельхейму. Когда лёд подошёл близко к царству огня, то он стал таять. Искры, вылетающие из Муспельхейма, смешались с растаявшим льдом и вдохнули в него жизнь. Так появился <b>Имир</b>, первое живое существо.</p>';
            massiveStory[3] = '<img  src="Picture/nifel.jpg">'
            delete massiveStory[4];
            delete massiveStory[5];
            div.innerHTML = massiveStory.join('');
            break;
        default:
            break;
    }
};

document.querySelector('#Imir').addEventListener('click', (f) => chooseGiants(giants = 'Imir'));
document.querySelector('#Jötunn').addEventListener('click', (f) => chooseGiants(giants = 'Jötunn'));
document.querySelector('#Loki').addEventListener('click', (f) => chooseGiants(giants = 'Loki'));
document.querySelector('#Angrboda').addEventListener('click', (f) => chooseGiants(giants = 'Angrboda'));
document.querySelector('#Fenrir').addEventListener('click', (f) => chooseGiants(giants = 'Fenrir'));
document.querySelector('#brothers').addEventListener('click', (f) => chooseGiants(giants = 'brothers'));
document.querySelector('#Jörmungandr').addEventListener('click', (f) => chooseGiants(giants = 'Jörmungandr'));
